mapping:
(
    $cfg := params.cfg;
    $tags := $cfg.affiliation_tags;
{
    "title": $."article title",
    "doi": $.DOI,
    "oa_category": $."journal publishing model",
    "authors_inst": (
        $n := $trim($."corresponding author name");
        $exists($n) and $n != "" ?
        (
            $parts := $split($n, " ");
            [
                {
                    "first_name": $join(
                    $filter($parts, function($v, $i){ $i < $count($parts) - 1 }),
                    " "
                    ),
                    "last_name": $parts[-1]
                }
            ]
        ) : []
),
    "authors": $."contributing authors",
    "greater_entity": (
        $n = $."journal title";
        $exists($n) and $n != "" ? 
        {
      "label": $."journal title",
      "identifiers": $.eISSN.{
        "type" : "issn",
        "value" : $
        } 
    }: null),
    "publisher": {
      "label": 'Springer Nature'
    },
    "pub_date": $."online first publication date",
    "pub_date_accepted": $."acceptance date",
    "pub_date_submitted": $."submission date",
    "funder": (
            $n := $trim($.FundNames);
            $exists($n) and $n != "" ? (
		        $names := $filter(
                $map($split(FundNames, ","), $trim),
            function($v){ $v != "" }
            );
            $ids   := $map($split(FundRefIDs, ","), $trim);
            $map($names, function($name, $i){
                {
                "label": $name,
                "doi": $ids[$i]
                }
            })[]
            ) : []
),
    "pub_type": "article",
    "license": $contains($."Licence type(s)","CC BY")? "CC-BY" : null,
    "status": 1,
    "abstract": $.abstract,
	"add_info": $."approval comment",
	"contract": "DEAL Springer"
}
)
